import styled, { css } from 'styled-components';

import IContainerProps from './interfaces/styles/IContainerProps';

import Tooltip from '../Tooltip';

export const Container = styled.div<IContainerProps>`
  background: #ffffff;
  border-radius: 10px;
  padding: 16px;
  width: 100%;

  color: #666360;
  border: 1px solid #dcdde0;

  display: flex;
  align-items: center;

  & + div {
    margin-top: 8px;
  }

  ${props =>
    props.isErrored &&
    css`
      border-color: #c33030;
    `}

  ${props =>
    props.isFocused &&
    css`
      color: #0066ff;
      border-color: #0066ff;
    `}

  ${props =>
    props.isFilled &&
    css`
      color: #0066ff;
    `}

  input {
    flex: 1;
    background: transparent;
    border: 0;
    color: #f4ede8;
    padding-top: 1px;

    &::placeholder {
      color: #666360;
    }

    & + input {
      margin-top: 8px;
    }
  }

  svg {
    margin-right: 16px;
    color: #0066ff;
    opacity: 0.20;
  }
`;

export const Error = styled(Tooltip)`
  height: 20px;
  margin-left: 16px;

  cursor: pointer;

  svg {
    margin: 0;
  }

  span {
    background: #ea0b0b;
    color: #fff;

    &::before {
      border-color: #ea0b0b transparent;
    }
  }
`;
