import React from 'react';
import useSWR, { mutate } from 'swr';

const getUsers = url => fetch(url).then(data => data.json());

const API_ENDPOINT = 'https://yesno.wtf/api';

const toggleAnswer = state => {
  switch (state) {
    case 'yes':
      state = 'no';
      break;
    case 'no':
      state = 'yes';
      break;
  }
  return state;
};

export const List = () => {
  const { data, error } = useSWR(API_ENDPOINT, getUsers);
  if (error) return <div>failed to load</div>;
  if (!data) return <div>loading...</div>;
  return (
    <>
      <h1>{data.answer}</h1>
      <button
        onClick={() => {
          mutate(API_ENDPOINT, { ...data, answer: toggleAnswer(data.answer) });
        }}
      >
        Change answer
      </button>
    </>
  );
};

export default List;
