import styled from 'styled-components';
import { shade } from 'polished';

export const Container = styled.button`
  background: #0066ff;
  height: 56px;
  border-radius: 10px;
  border: 0;
  padding: 0 16px;
  color: #ffffff;
  width: 100%;
  font-size:18px;
  font-weight: 500;
  margin-top: 16px;
  transition: background 0.2s;

  &:hover {
    background: ${shade(0.2, '#0066ff')};
  }
`;
